import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import { gsap } from 'gsap';
import './Navbar.css';
import logo from '../Assets/logo.png';
import profile from '../Assets/profile.png';
import cart_icon from '../Assets/cart_icon.png';

const Navbar = () => {
  const [menu, setMenu] = useState('Home');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const menuRef = useRef(null);
  const iconsRef = useRef(null);

  useEffect(() => {
    // Animate icons with GSAP
    gsap.fromTo(
      iconsRef.current.children,
      { opacity: 0, y: 20 },
      { opacity: 1, y: 0, duration: 0.5, stagger: 0.2 }
    );
  }, []);

  useEffect(() => {
    // Animate menu when it opens or closes
    gsap.to(menuRef.current, {
      x: isMenuOpen ? 0 : '-100%',
      duration: 0.3,
      ease: 'power2.out'
    });
  }, [isMenuOpen]);

  const handleMenuToggle = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div className='navbar'>
      <div className='nav-logo'>
        <img src={logo} alt='logo' />
        <p>KK CLOTHING</p>
      </div>
      <div className='nav-menu-toggle' onClick={handleMenuToggle}>
        â˜°
      </div>
      <ul className={`nav-menu ${isMenuOpen ? 'active' : ''}`} ref={menuRef}>
        <li onClick={() => setMenu('Home')}>
          <Link to='/' style={{ textDecoration: 'none', color: 'inherit' }}>Home</Link>
          {menu === 'Home' ? <hr /> : null}
        </li>
        <li onClick={() => setMenu('Shop')}>
          <Link to='/shop' style={{ textDecoration: 'none', color: 'inherit' }}>Shop All</Link>
          {menu === 'Shop' ? <hr /> : null}
        </li>
        <li onClick={() => setMenu('Men')}>
          <Link to='/men' style={{ textDecoration: 'none', color: 'inherit' }}>Men</Link>
          {menu === 'Men' ? <hr /> : null}
        </li>
        <li onClick={() => setMenu('Women')}>
          <Link to='/women' style={{ textDecoration: 'none', color: 'inherit' }}>Women</Link>
          {menu === 'Women' ? <hr /> : null}
        </li>
        <li onClick={() => setMenu('Contact')}>
          <Link to='/contact' style={{ textDecoration: 'none', color: 'inherit' }}>Contact Us</Link>
          {menu === 'Contact' ? <hr /> : null}
        </li>
      </ul>
      <div className='nav-login-cart' ref={iconsRef}>
        <Link to='/Login'><img src={profile} alt='profile icon' className='icon' /></Link>
        <Link to='/cart'><img src={cart_icon} alt='cart icon' /></Link>
        <div className='nav-cart-count'>0</div>
      </div>
    </div>
  );
};

export default Navbar;
