// FRONTEND/src/Pages/Profile.jsx
import React, { useState, useEffect } from 'react';
//import './Profile.css'; // Create a CSS file for styling
import axios from 'axios'; // Ensure you have axios installed

const Profile = () => {
  const [user, setUser] = useState(null);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
  });

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get('/api/user/profile', {
          headers: { 'Authorization': `Bearer ${localStorage.getItem('auth-token')}` }
        });
        setUser(response.data);
        setFormData({ name: response.data.name, email: response.data.email });
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put('/api/user/profile', formData, {
        headers: { 'Authorization': `Bearer ${localStorage.getItem('auth-token')}` }
      });
      setEditing(false); // Exit editing mode
      setUser({ ...user, ...formData }); // Update user data in state
    } catch (error) {
      console.error('Error updating user data:', error);
    }
  };

  const handlePasswordChange = async (e) => {
    e.preventDefault();
    try {
      await axios.put('/api/user/change-password', { password: formData.password }, {
        headers: { 'Authorization': `Bearer ${localStorage.getItem('auth-token')}` }
      });
      alert('Password changed successfully');
      setFormData({ ...formData, password: '' }); // Clear password field
    } catch (error) {
      console.error('Error changing password:', error);
    }
  };

  if (!user) return <div>Loading...</div>;

  return (
    <div className="profile">
      <h1>User Profile</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            disabled={!editing}
          />
        </div>
        <div className="form-group">
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            disabled={!editing}
          />
        </div>
        {editing ? (
          <button type="submit">Save Changes</button>
        ) : (
          <button type="button" onClick={() => setEditing(true)}>Edit Profile</button>
        )}
      </form>

      <h2>Change Password</h2>
      <form onSubmit={handlePasswordChange}>
        <div className="form-group">
          <label>New Password:</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
          />
        </div>
        <button type="submit">Change Password</button>
      </form>
    </div>
  );
};

export default Profile;
