import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { gsap } from "gsap"; // GSAP for animations
import "./Registration.css"; // Import your CSS for styling

const Registration = () => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
  });
  const [showPassword, setShowPassword] = useState(false);
  const formRef = useRef(null);

  // GSAP animation for form elements
  useEffect(() => {
    const elements = formRef.current.querySelectorAll(".animate");
    gsap.fromTo(
      elements,
      { x: () => `${Math.random() * 200 - 100}px`, y: () => `${Math.random() * 200 - 100}px`, opacity: 0 },
      { x: 0, y: 0, opacity: 1, duration: 0.5, stagger: 0.1 }
    );
  }, []);

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/register", formData);
      alert(response.data.message || "Registration successful!"); // Display success message
      setFormData({ firstName: "", lastName: "", email: "", password: "" }); // Clear form
    } catch (error) {
      console.error("Registration error:", error);
      alert(error.response?.data?.message || "Registration failed!"); // Display error message
    }
  };

  // Toggle password visibility
  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  return (
    <section>
      <div className="reg-container" ref={formRef}>
        <h1 className="heading h2">Register</h1>
        <form onSubmit={handleSubmit} className="form">
          <div className="input animate">
            <input
              type="text"
              name="firstName"
              value={formData.firstName}
              onChange={handleChange}
              required
              placeholder="First Name"
            />
          </div>
          <div className="input animate">
            <input
              type="text"
              name="lastName"
              value={formData.lastName}
              onChange={handleChange}
              required
              placeholder="Last Name"
            />
          </div>
          <div className="input animate">
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              placeholder="Email"
            />
          </div>
          <div className="input animate">
            <input
              type={showPassword ? "text" : "password"}
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              placeholder="Password"
            />
            <button type="button" onClick={togglePasswordVisibility}>
              {showPassword ? "Hide" : "Show"}
            </button>
          </div>
          <button type="submit" className="form__submit">
            Create Account
          </button>
        </form>
      </div>
    </section>
  );
};

export default Registration;
