// FRONTEND/src/Components/ContactUs/ContactUs.jsx
import React, { useState, useEffect } from 'react';
import AOS from 'aos';
import 'aos/dist/aos.css'; // AOS styles
import './ContactUs.css'; // Corrected CSS import
import emailjs from 'emailjs-com'; // EmailJS for sending emails

const ContactUs = () => {
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    AOS.init({ duration: 1000, once: true }); // Initialize AOS animations
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Send email using EmailJS
    emailjs
      .sendForm(
        'service_id',  // Replace with your service ID
        'template_id',  // Replace with your template ID
        e.target,       // The form element
        'user_id'       // Replace with your user ID from EmailJS
      )
      .then(
        (result) => {
          setSuccessMessage('Your message has been sent successfully!');
          setFormData({ name: '', email: '', message: '' }); // Clear form after success
        },
        (error) => {
          setErrorMessage('There was an error sending your message. Please try again.');
        }
      );
  };

  return (
    <div className="contact-us-container" data-aos="fade-up">
      <h3 data-aos="fade-up">Contact Us</h3>
      <form onSubmit={handleSubmit}>
        <div data-aos="fade-up">
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>
        <div data-aos="fade-up">
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div data-aos="fade-up">
          <label>Message:</label>
          <textarea
            name="message"
            value={formData.message}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" data-aos="fade-up">
          Submit
        </button>
      </form>

      {/* Display Success or Error Messages */}
      {successMessage && <div className="success-message">{successMessage}</div>}
      {errorMessage && <div className="error-message">{errorMessage}</div>}
    </div>
  );
};

export default ContactUs;
