// FRONTEND/src/Components/Navbar/Navbar.jsx
import React, { useState, useEffect, useContext } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Navbar.css';
import logo from '../Assets/logo.png';
import ProfileIcon from '../Assets/profile.png';
import cart_icon from '../Assets/cart_icon.png';
import { ShopContext } from '../../Context/ShopContext';

const Navbar = () => {
  const location = useLocation();
  const [menu, setMenu] = useState('Home');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const { getTotalCartItems } = useContext(ShopContext);

  // Check if user is logged in
  const isLoggedIn = localStorage.getItem('auth-token');

  // Update menu state based on the current location
  useEffect(() => {
    const path = location.pathname;
    if (path === '/') setMenu('Home');
    else if (path === '/shop') setMenu('Shop');
    else if (path === '/men') setMenu('Men');
    else if (path === '/women') setMenu('Women');
    else if (path === '/contact') setMenu('Contact');
    else if (/^\/aboutus$/.test(path)) setMenu('About Us');
    else if (/^\/return/.test(path)) setMenu('Return');
    else if (/^\/shipping$/.test(path)) setMenu('Shipping');
    else if (/^\/terms$/.test(path)) setMenu('Terms');
    else if (path.startsWith('/product')) setMenu(''); // Clear menu if on product page
  }, [location]);

  const handleMenuToggle = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const isProductPage = location.pathname.startsWith('/product');

  const handleLogout = () => {
    localStorage.removeItem('auth-token');
    window.location.replace('/');
  };

  return (
    <div className={`navbar ${isProductPage ? 'no-underline' : ''}`}>
      <div className='nav-logo'>
        <img src={logo} alt='logo' />
        <p>OVERLAYS</p>
      </div>
      <div className='nav-menu-toggle' onClick={handleMenuToggle}>
        â˜°
      </div>
      <ul className={`nav-menu ${isMenuOpen ? 'active' : ''}`}>
        <li onClick={() => setMenu('Home')}>
          <Link to='/' className={menu === 'Home' ? 'active-link' : ''}>Home</Link>
          {menu === 'Home' && <hr />}
        </li>
        <li onClick={() => setMenu('Shop')}>
          <Link to='/shop' className={menu === 'Shop' ? 'active-link' : ''}>Shop All</Link>
          {menu === 'Shop' && <hr />}
        </li>
        <li onClick={() => setMenu('Men')}>
          <Link to='/men' className={menu === 'Men' ? 'active-link' : ''}>Men</Link>
          {menu === 'Men' && <hr />}
        </li>
        <li onClick={() => setMenu('Women')}>
          <Link to='/women' className={menu === 'Women' ? 'active-link' : ''}>Women</Link>
          {menu === 'Women' && <hr />}
        </li>
        <li onClick={() => setMenu('Contact')}>
          <Link to='/contact' className={menu === 'Contact' ? 'active-link' : ''}>Contact Us</Link>
          {menu === 'Contact' && <hr />}
        </li>
        <li onClick={() => setMenu('About Us')}>
          <Link to='/aboutus' className={menu === 'About Us' ? 'active-link' : ''}>About Us</Link>
          {menu === 'About Us' && <hr />}
        </li>
      </ul>
      <div className="nav-login-cart">
        {isLoggedIn ? (
          <>
            <Link to="/profile">
              <img src={ProfileIcon} alt="Profile" className="profile-icon" />
            </Link>
            <button onClick={handleLogout}>Logout</button>
          </>
        ) : (
          <Link to='/login' style={{ textDecoration: 'none' }}>
            <button>Login</button>
          </Link>
        )}
        <Link to="/cart">
          <img src={cart_icon} alt="cart" />
        </Link>
        <div className="nav-cart-count">{getTotalCartItems()}</div>
      </div>
    </div>
  );
};

export default Navbar;
