import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { gsap } from 'gsap';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for redirection
import './Registration.css';

const Registration = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
  });
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const formRef = useRef(null);
  const navigate = useNavigate(); // Initialize useNavigate

  useEffect(() => {
    const elements = formRef.current.querySelectorAll('.animate');
    gsap.fromTo(
      elements,
      {
        x: () => `${Math.random() * 200 - 100}px`,
        y: () => `${Math.random() * 200 - 100}px`,
        opacity: 0,
      },
      { x: 0, y: 0, opacity: 1, duration: 0.5, stagger: 0.1 }
    );
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(''); // Clear previous errors
    setSuccess(''); // Clear previous success messages
    try {
      await axios.post('http://localhost:5000/auth/register', formData); // Update with the correct backend URL
      setSuccess('Registration successful! Please login with your credentials.'); // Display success message
      setFormData({ firstName: '', lastName: '', email: '', password: '' }); // Reset form
      
      // Redirect to login page after a short delay
      setTimeout(() => {
        navigate('/login');
      }, 2000);
    } catch (error) {
      console.error('Registration error:', error);
      setError(error.response?.data?.message || 'There was an error registering the user!'); // Display error
    }
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  return (
    <section>
      <div className="reg-container" ref={formRef}>
        <div className="page-header animate">
          <div className="page-header__text-wrapper text-container">
            <h1 className="heading h2">Register</h1>
            <p>Please fill in the fields below:</p>
          </div>
        </div>
        {error && <div className="error-message">{error}</div>} {/* Display error message */}
        {success && <div className="success-message">{success}</div>} {/* Display success message */}
        <div className="page-content page-content--small">
          <div className="account__block-list">
            <div className="account__block-item">
              <form onSubmit={handleSubmit} className="form">
                <div className="input animate">
                  <input
                    type="text"
                    id="firstName"
                    name="firstName"
                    className="input__field"
                    value={formData.firstName}
                    onChange={handleChange}
                    required
                    autoComplete="given-name"
                  />
                  <label htmlFor="firstName" className="input__label">First name</label>
                </div>
                <div className="input animate">
                  <input
                    type="text"
                    id="lastName"
                    name="lastName"
                    className="input__field"
                    value={formData.lastName}
                    onChange={handleChange}
                    required
                    autoComplete="family-name"
                  />
                  <label htmlFor="lastName" className="input__label">Last name</label>
                </div>
                <div className="input animate">
                  <input
                    type="email"
                    id="email"
                    name="email"
                    className="input__field"
                    value={formData.email}
                    onChange={handleChange}
                    required
                    autoComplete="email"
                  />
                  <label htmlFor="email" className="input__label">E-mail</label>
                </div>
                <div className="input animate">
                  <input
                    type={showPassword ? 'text' : 'password'}
                    id="password"
                    name="password"
                    className="input__field"
                    value={formData.password}
                    onChange={handleChange}
                    required
                    autoComplete="new-password"
                  />
                  <label htmlFor="password" className="input__label">Password</label>
                  <button
                    type="button"
                    className="input__field-link link text--xsmall text--subdued toggle-password"
                    onClick={togglePasswordVisibility}
                  >
                    {showPassword ? 'Hide' : 'Show'}
                  </button>
                </div>
                <button type="submit" className="form__submit button button--primary button--full animate">
                  <span className="loader-button__text">Create account</span>
                </button>
                <span className="form__secondary-action text--subdued animate">
                  Already have an account? <a href="/login" className="link">Login</a>
                </span>
              </form>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Registration;
