// ProductDisplay.jsx
import React, { useContext } from 'react';
import { useParams } from 'react-router-dom';
import { ShopContext } from '../../Context/ShopContext';
import './ProductDisplay.css'; // Assuming there's a CSS file

const ProductDisplay = () => {
  const { productId } = useParams(); // Get the product ID from the URL
  const { all_product, addToCart } = useContext(ShopContext); // Get product data and addToCart function

  const product = all_product.find((item) => item.id === Number(productId)); // Find the product by ID

  if (!product) {
    return <div>Product not found</div>; // Handle case where product does not exist
  }

  return (
    <div className='product-display'>
      <h1>{product.name}</h1>
      <img src={product.image} alt={product.name} />
      <div className='product-details'>
        <p>Old Price: ${product.old_price}</p>
        <p>New Price: ${product.new_price}</p>
        <p>{product.description}</p> {/* Add a description field in your product data if needed */}
        <button onClick={() => addToCart(product.id)}>ADD TO CART</button> {/* Add to cart button */}
      </div>
    </div>
  );
};

export default ProductDisplay;
