import React, { useEffect } from 'react';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import Freedom from '../Components/Freedom/Freedom';
import Grid from '../Components/Grid/Grid';
import Popular from '../Components/Popular/Popular';
import { WhatsAppChatWidget } from '../Components/WhatsAppChat/WhatsAppChatWidget';
import Round from '../Components/Round/Round';

// Register the ScrollTrigger plugin
gsap.registerPlugin(ScrollTrigger);

const Home = () => {
  useEffect(() => {
    // GSAP animations with ScrollTrigger
    gsap.fromTo(
      '.freedom-section',
      { opacity: 0, y: 50 },
      {
        opacity: 1,
        y: 0,
        duration: 1,
        scrollTrigger: {
          trigger: '.freedom-section',
          start: 'top 80%',
        },
      }
    );

    gsap.fromTo(
      '.round-section',
      { opacity: 0, y: 50 },
      {
        opacity: 1,
        y: 0,
        duration: 1,
        scrollTrigger: {
          trigger: '.round-section',
          start: 'top 80%',
        },
      }
    );

    gsap.fromTo(
      '.popular-section',
      { opacity: 0, y: 50 },
      {
        opacity: 1,
        y: 0,
        duration: 1,
        scrollTrigger: {
          trigger: '.popular-section',
          start: 'top 80%',
        },
      }
    );

    gsap.fromTo(
      '.grid-section',
      { opacity: 0, y: 50 },
      {
        opacity: 1,
        y: 0,
        duration: 1,
        scrollTrigger: {
          trigger: '.grid-section',
          start: 'top 80%',
        },
      }
    );

    gsap.fromTo(
      '.whatsapp-chat-widget-section',
      { opacity: 0, y: 50 },
      {
        opacity: 1,
        y: 0,
        duration: 1,
        scrollTrigger: {
          trigger: '.whatsapp-chat-widget-section',
          start: 'top 80%',
        },
      }
    );
  }, []);

  return (
    <div>
      <div className="freedom-section" style={{ backgroundColor: 'lightblue' }}>
        <Freedom />
      </div>
      <div className="round-section" style={{ backgroundColor: 'lightgreen' }}>
        <Round />
      </div>
      <br />
      <div className="popular-section" style={{ backgroundColor: 'lightyellow' }}>
        <Popular />
      </div>
      <br />
      <div className="grid-section" style={{ backgroundColor: 'lightcoral' }}>
        <Grid />
      </div>
      <br />
      <div className="whatsapp-chat-widget-section" style={{ backgroundColor: 'lightgray' }}>
        <WhatsAppChatWidget />
      </div>
    </div>
  );
};

export default Home;
