import React, { useEffect } from 'react';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import './ColorPage.css';

// Register the ScrollTrigger plugin
gsap.registerPlugin(ScrollTrigger);

const ColorPage = () => {
  useEffect(() => {
    // GSAP timeline for animations
    const tl = gsap.timeline({
      scrollTrigger: {
        trigger: '.color-page',
        start: 'top top',
        end: 'bottom top',
        scrub: 1,
        pin: true,
        anticipatePin: 1,
      },
    });

    // Animation for the main container
    tl.fromTo(
      '.color-page',
      { opacity: 0, y: 100 },
      { opacity: 1, y: 0, duration: 1 }
    );

    // Animation for the heading
    tl.fromTo(
      '.color-page h1',
      { opacity: 0, y: -50 },
      { opacity: 1, y: 0, duration: 1 },
      '-=0.5' // Overlap with previous animation
    );

    // Animation for the paragraph
    tl.fromTo(
      '.color-page p',
      { opacity: 0, y: 30 },
      { opacity: 1, y: 0, duration: 1 },
      '-=0.5' // Overlap with previous animation
    );

    // Animation on scroll up
    ScrollTrigger.create({
      trigger: '.color-page',
      start: 'top top',
      end: 'bottom top',
      onUpdate: (self) => {
        if (self.direction === -1) { // When scrolling up
          gsap.to('.color-page', { backgroundColor: '#ffcccb', duration: 0.5 });
        } else { // When scrolling down
          gsap.to('.color-page', { backgroundColor: '#f0e6f6', duration: 0.5 });
        }
      },
    });
  }, []);

  return (
    <div className="color-page">
      <h1>Welcome to the Colorful Page!</h1>
      <p>This section has layered animations for a unique effect.</p>
    </div>
  );
};

export default ColorPage;
