import React, { useState, useEffect } from 'react';
import { useUser } from '../Context/UserContext'; // Adjust based on the actual file location
import AOS from 'aos';
import 'aos/dist/aos.css';
import './ContactUs.css';

const ContactUs = () => {
  const { user, loading } = useUser(); // Fetch user from context
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });

  useEffect(() => {
    AOS.init({ duration: 1000, once: true });
  }, []);

  useEffect(() => {
    if (user) {
      setFormData({ ...formData, name: user.firstName, email: user.email });
    }
  }, [user]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="contact-us-container" data-aos="fade-up">
      <h3 data-aos="fade-up">Contact Us</h3>
      <form onSubmit={handleSubmit}>
        <div data-aos="fade-up">
          <label>Name:</label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} required />
        </div>
        <div data-aos="fade-up">
          <label>Email:</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} required />
        </div>
        <div data-aos="fade-up">
          <label>Message:</label>
          <textarea name="message" value={formData.message} onChange={handleChange} required />
        </div>
        <button type="submit" data-aos="fade-up">Submit</button>
      </form>
    </div>
  );
};

export default ContactUs;
