import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../Pages/profile.css';

const Profile = () => {
  const [userData, setUserData] = useState({ firstName: '', lastName: '', email: '' });
  const [newPassword, setNewPassword] = useState('');
  const [currentPassword, setCurrentPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const [isEditing, setIsEditing] = useState({ firstName: false, lastName: false, email: false });

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem('auth-token');
        const response = await axios.get('http://localhost:5000/auth/user', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setUserData(response.data);
      } catch (err) {
        console.error('Error fetching user data:', err);
      }
    };

    fetchUserData();
  }, []);

  const handleFieldUpdate = async (field) => {
    setError('');
    setSuccess('');

    try {
      const token = localStorage.getItem('auth-token');
      const response = await axios.put(`http://localhost:5000/auth/update-${field}`, { [field]: userData[field] }, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setSuccess(response.data.message);
      setIsEditing({ ...isEditing, [field]: false });
    } catch (err) {
      console.error(`Error updating ${field}:`, err.response);
      setError(err.response?.data?.message || `Failed to update ${field}. Please try again.`);
    }
  };

  const handleChangePassword = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const token = localStorage.getItem('auth-token');
      const response = await axios.patch('http://localhost:5000/auth/change-password', { currentPassword, newPassword }, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setSuccess(response.data.message);
      setCurrentPassword('');
      setNewPassword('');
    } catch (err) {
      console.error('Error changing password:', err);
      setError(err.response?.data?.message || 'Failed to change password. Please try again.');
    }
  };

  return (
    <div className="profile-container">
      <h1>Profile</h1>
      <form>
        <div className="input-group">
          <label>First Name:</label>
          {isEditing.firstName ? (
            <>
              <input
                type="text"
                value={userData.firstName}
                onChange={(e) => setUserData({ ...userData, firstName: e.target.value })}
                required
              />
              <button type="button" onClick={() => handleFieldUpdate('firstName')}>Save</button>
            </>
          ) : (
            <div className="display-container">
              <span>{userData.firstName}</span>
              <button type="button" onClick={() => setIsEditing({ ...isEditing, firstName: true })}>
                ✏️
              </button>
            </div>
          )}
        </div>

        <div className="input-group">
          <label>Last Name:</label>
          {isEditing.lastName ? (
            <>
              <input
                type="text"
                value={userData.lastName}
                onChange={(e) => setUserData({ ...userData, lastName: e.target.value })}
                required
              />
              <button type="button" onClick={() => handleFieldUpdate('lastName')}>Save</button>
            </>
          ) : (
            <div className="display-container">
              <span>{userData.lastName}</span>
              <button type="button" onClick={() => setIsEditing({ ...isEditing, lastName: true })}>
                ✏️
              </button>
            </div>
          )}
        </div>

        <div className="input-group">
          <label>Email:</label>
          {isEditing.email ? (
            <>
              <input
                type="email"
                value={userData.email}
                onChange={(e) => setUserData({ ...userData, email: e.target.value })}
                required
              />
              <button type="button" onClick={() => handleFieldUpdate('email')}>Save</button>
            </>
          ) : (
            <div className="display-container">
              <span>{userData.email}</span>
              <button type="button" onClick={() => setIsEditing({ ...isEditing, email: true })}>
                ✏️
              </button>
            </div>
          )}
        </div>

        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}
      </form>

      <h2>Change Password</h2>
      <form onSubmit={handleChangePassword}>
        <div className="input-group">
          <label>Current Password:</label>
          <input
            type="password"
            value={currentPassword}
            onChange={(e) => setCurrentPassword(e.target.value)}
            required
          />
        </div>
        <div className="input-group">
          <label>New Password:</label>
          <input
            type="password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Change Password</button>
        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}
      </form>
    </div>
  );
};

export default Profile;
