// FRONTEND/src/Context/ShopContext.jsx
import React, { createContext, useState, useEffect } from "react";
import all_product from "../Components/Assets/all_product";
import axios from 'axios';

export const ShopContext = createContext(null);

const getDefaultCart = () => {
    let cart = {};
    for (let index = 0; index < all_product.length + 1; index++) {
        cart[index] = 0;
    }
    return cart;
};

const ShopContextProvider = (props) => {
    const [cartItems, setCartItems] = useState(() => {
        const savedCart = localStorage.getItem('cartItems');
        return savedCart ? JSON.parse(savedCart) : getDefaultCart();
    });

    // Update localStorage whenever cartItems change
    useEffect(() => {
        localStorage.setItem('cartItems', JSON.stringify(cartItems));
    }, [cartItems]);

    // Fetch the user's cart from the server if they are logged in
    useEffect(() => {
        const fetchUserCart = async () => {
            const token = localStorage.getItem('auth-token');
            if (token) {
                try {
                    const response = await axios.get('http://localhost:5000/auth/user', {
                        headers: { 'Authorization': `Bearer ${token}` },
                    });
                    if (response.data.cart) {
                        setCartItems(response.data.cart);
                    }
                } catch (error) {
                    console.error('Error fetching user cart:', error);
                }
            }
        };

        fetchUserCart();
    }, []);

    const addToCart = (itemId) => {
        setCartItems((prev) => ({ ...prev, [itemId]: prev[itemId] + 1 }));
    };

    const removeFromCart = (itemId) => {
        setCartItems((prev) => ({ ...prev, [itemId]: prev[itemId] - 1 }));
    };

    const getTotalCartAmount = () => {
        let totalAmount = 0;
        for (const item in cartItems) {
            if (cartItems[item] > 0) {
                let itemInfo = all_product.find((product) => product.id === Number(item));
                totalAmount += cartItems[item] * itemInfo.new_price;
            }
        }
        return totalAmount;
    };

    const getTotalCartItems = () => {
        let totalItem = 0;
        for (const item in cartItems) {
            if (cartItems[item] > 0) {
                totalItem += cartItems[item];
            }
        }
        return totalItem;
    };

    const resetCart = () => {
        const defaultCart = getDefaultCart();
        setCartItems(defaultCart);
        localStorage.setItem('cartItems', JSON.stringify(defaultCart));
    };

    const saveCartToServer = async () => {
        const token = localStorage.getItem('auth-token');
        if (token) {
            try {
                await axios.put(
                    'http://localhost:5000/auth/update-cart',
                    { cart: cartItems },
                    { headers: { 'Authorization': `Bearer ${token}` } }
                );
            } catch (error) {
                console.error('Error saving cart to server:', error);
            }
        }
    };

    const handleLogout = async () => {
        await saveCartToServer();
        resetCart(); // Reset cart locally
        localStorage.removeItem('auth-token'); // Clear auth token on logout
    };

    const contextValue = {
        getTotalCartItems,
        getTotalCartAmount,
        all_product,
        cartItems,
        addToCart,
        removeFromCart,
        resetCart,
        handleLogout,
    };

    return (
        <ShopContext.Provider value={contextValue}>
            {props.children}
        </ShopContext.Provider>
    );
};

export default ShopContextProvider;
