import React, { useState, useEffect } from 'react';
import './Login.css';

const LoginForm = ({ switchToRecover }) => {
  const [showPassword, setShowPassword] = useState(false);

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  return (
    <div id="login-form-container" className="form-container">
      <div className="page-header">
        <div className="page-header__text-wrapper text-container">
          <h1 className="heading h2">Login</h1>
          <p>Please enter your e-mail and password:</p>
        </div>
      </div>
      <div className="page-content page-content--small">
        <div className="account__block-list">
          <div className="account__block-item">
            <form method="post" action="/account/login" id="customer_login" acceptCharset="UTF-8" data-login-with-shop-sign-in="true" name="login" className="form">
              <input type="hidden" name="form_type" value="customer_login" />
              <input type="hidden" name="utf8" value="✓" />
              <div className="input">
                <input type="email" id="customer[email]" autoComplete="email" className="input__field" name="customer[email]" required />
                <label htmlFor="customer[email]" className="input__label">E-mail</label>
              </div>
              <div className="input">
                <input type={showPassword ? 'text' : 'password'} id="customer[password]" className="input__field" name="customer[password]" required autoComplete="current-password" />
                <label htmlFor="customer[password]" className="input__label">Password</label>
                <button type="button" className="input__field-link link text--xsmall text--subdued toggle-password" onClick={togglePasswordVisibility}>
                  {showPassword ? 'Hide' : 'Show'}
                </button>
              </div>
              <button type="button" className="input__field-link link text--xsmall text--subdued forgot-password" onClick={switchToRecover}>Forgot password?</button>
              <button type="submit" className="form__submit button button--primary button--full">
                <span className="loader-button__text">Login</span>
              </button>
              <span className="form__secondary-action text--subdued">New customer? <a href="/Register" className="link">Create an account</a></span>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

const RecoverForm = ({ switchToLogin }) => {
  return (
    <div id="recover-form-container" className="form-container">
      <div className="page-header">
        <div className="page-header__text-wrapper text-container">
          <h1 className="heading h2">Recover password</h1>
          <p>Please enter your e-mail:</p>
        </div>
      </div>
      <div className="page-content page-content--small">
        <form method="post" action="/account/recover" acceptCharset="UTF-8" name="recover" className="form">
          <input type="hidden" name="form_type" value="recover_customer_password" />
          <input type="hidden" name="utf8" value="✓" />
          <div className="input">
            <input type="email" id="customer[recover_email]" className="input__field" name="email" required />
            <label htmlFor="customer[recover_email]" className="input__label">E-mail</label>
          </div>
          <button type="submit" className="form__submit button button--primary button--full">
            <span className="loader-button__text">Recover</span>
          </button>
          <span className="form__secondary-action text--subdued">Remember your password? <button type="button" className="link" onClick={switchToLogin}>Back to login</button></span>
        </form>
      </div>
    </div>
  );
};

const Login = () => {
  const [isRecovering, setIsRecovering] = useState(false);

  const switchToRecover = () => setIsRecovering(true);
  const switchToLogin = () => setIsRecovering(false);

  useEffect(() => {
    if (window.location.hash === '#recover') {
      setIsRecovering(true);
    }
  }, []);

  return (
    <div className="login-page">
      {isRecovering ? <RecoverForm switchToLogin={switchToLogin} /> : <LoginForm switchToRecover={switchToRecover} />}
    </div>
  );
};

export default Login;
