import React, { useEffect } from 'react';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import './ColorPage.css'; // Make sure this file exists and contains necessary styles

// Register the ScrollTrigger plugin
gsap.registerPlugin(ScrollTrigger);

const ColorPage = () => {
  useEffect(() => {
    // Animation for the main container
    gsap.fromTo(
      '.color-page',
      { opacity: 0, y: 50 },
      {
        opacity: 1,
        y: 0,
        duration: 1,
        scrollTrigger: {
          trigger: '.color-page',
          start: 'top 80%',
        },
      }
    );

    // Animation for the heading
    gsap.fromTo(
      '.color-page h1',
      { opacity: 0, y: -50 },
      {
        opacity: 1,
        y: 0,
        duration: 1,
        delay: 0.5,
        scrollTrigger: {
          trigger: '.color-page h1',
          start: 'top 80%',
        },
      }
    );

    // Animation for the paragraph
    gsap.fromTo(
      '.color-page p',
      { opacity: 0, y: 30 },
      {
        opacity: 1,
        y: 0,
        duration: 1,
        delay: 1,
        scrollTrigger: {
          trigger: '.color-page p',
          start: 'top 80%',
        },
      }
    );
  }, []);

  return (
    <div className="color-page">
      <h1>Welcome to the Colorful Page!</h1>
      <p>This section has layered animations for a unique effect.</p>
    </div>
  );
};

export default ColorPage;
