import React, { useState } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import './ResetPassword.css';

const ResetPassword = () => {
  const [otp, setOtp] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [isOtpVerified, setIsOtpVerified] = useState(false); // Track OTP verification state
  const location = useLocation();

  const handleOtpSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setMessage('');

    const token = new URLSearchParams(location.search).get('token'); // Get OTP token from query params

    try {
      const response = await axios.post('http://localhost:5000/auth/verify-otp', {
        otp,
        token,
      });
      setMessage(response.data.message);
      setIsOtpVerified(true); // Set OTP as verified
    } catch (error) {
      setError(error.response?.data?.message || 'Invalid OTP or error.');
    }
  };

  const handlePasswordSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setMessage('');

    if (newPassword !== confirmPassword) {
      setError("Passwords don't match!");
      return;
    }

    const token = new URLSearchParams(location.search).get('token'); // Get OTP token from query params

    try {
      const response = await axios.post('http://localhost:5000/auth/reset-password', {
        newPassword,
        token,
      });
      setMessage(response.data.message);
    } catch (error) {
      setError(error.response?.data?.message || 'Error resetting password.');
    }
  };

  return (
    <div className="reset-password-container">
      <h2>Reset Password</h2>

      {/* Step 1: OTP Verification */}
      {!isOtpVerified ? (
        <form onSubmit={handleOtpSubmit}>
          <div>
            <label>OTP</label>
            <input
              type="text"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
              required
              placeholder="Enter OTP"
            />
          </div>
          <button type="submit">Verify OTP</button>
        </form>
      ) : (
        // Step 2: Reset Password
        <form onSubmit={handlePasswordSubmit}>
          <div>
            <label>New Password</label>
            <input
              type="password"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              required
              placeholder="Enter your new password"
            />
          </div>
          <div>
            <label>Confirm Password</label>
            <input
              type="password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              required
              placeholder="Confirm your new password"
            />
          </div>
          <button type="submit">Reset Password</button>
        </form>
      )}

      {message && <p>{message}</p>}
      {error && <p>{error}</p>}
    </div>
  );
};

export default ResetPassword;
