import React, { useState, useEffect } from 'react';
import './Login.css';

const LoginForm = ({ switchToRecover }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({ email: '', password: '' });

  const validateEmail = (email) => {
    return /\S+@\S+\.\S+/.test(email);
  };

  const validateForm = () => {
    let valid = true;
    const newErrors = { email: '', password: '' };

    if (!validateEmail(email)) {
      newErrors.email = 'Please enter a valid email.';
      valid = false;
    }
    if (password.length < 6) {
      newErrors.password = 'Password must be at least 6 characters long.';
      valid = false;
    }

    setErrors(newErrors);
    return valid;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      // Submit the form
    }
  };

  return (
    <div id="login-form-container" className="form-container">
      <div className="page-header">
        <div className="page-header__text-wrapper text-container">
          <h1 className="heading h2">Login</h1>
          <p>Please enter your e-mail and password:</p>
        </div>
      </div>
      <div className="page-content page-content--small">
        <div className="account__block-list">
          <div className="account__block-item">
            <form id="customer_login" className="form" onSubmit={handleSubmit}>
              <div className="input">
                <input 
                  type="email" 
                  className="input__field" 
                  value={email} 
                  onChange={(e) => setEmail(e.target.value)} 
                  required 
                />
                <label className="input__label">E-mail</label>
                {errors.email && <span className="error">{errors.email}</span>}
              </div>
              <div className="input">
                <input 
                  type="password" 
                  className="input__field" 
                  value={password} 
                  onChange={(e) => setPassword(e.target.value)} 
                  required 
                />
                <label className="input__label">Password</label>
                {errors.password && <span className="error">{errors.password}</span>}
                <button type="button" className="input__field-link link text--xsmall text--subdued" onClick={switchToRecover}>Forgot password?</button>
              </div>
              <button type="submit" className="form__submit button button--primary button--full">
                <span className="loader-button__text">Login</span>
              </button>
              <span className="form__secondary-action text--subdued">New customer? <a href="/Register" className="link">Create an account</a></span>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

const RecoverForm = ({ switchToLogin }) => {
  const [email, setEmail] = useState('');
  const [error, setError] = useState('');

  const validateEmail = (email) => {
    return /\S+@\S+\.\S+/.test(email);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateEmail(email)) {
      // Submit the form
      setError('');
    } else {
      setError('Please enter a valid email.');
    }
  };

  return (
    <div id="recover-form-container" className="form-container">
      <div className="page-header">
        <div className="page-header__text-wrapper text-container">
          <h1 className="heading h2">Recover password</h1>
          <p>Please enter your e-mail:</p>
        </div>
      </div>
      <div className="page-content page-content--small">
        <form className="form" onSubmit={handleSubmit}>
          <div className="input">
            <input 
              type="email" 
              className="input__field" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
              required 
            />
            <label className="input__label">E-mail</label>
            {error && <span className="error">{error}</span>}
          </div>
          <button type="submit" className="form__submit button button--primary button--full">
            <span className="loader-button__text">Recover</span>
          </button>
          <span className="form__secondary-action text--subdued">Remember your password? <button type="button" className="link" onClick={switchToLogin}>Back to login</button></span>
        </form>
      </div>
    </div>
  );
};

const Login = () => {
  const [isRecovering, setIsRecovering] = useState(false);

  const switchToRecover = () => setIsRecovering(true);
  const switchToLogin = () => setIsRecovering(false);

  useEffect(() => {
    if (window.location.hash === '#recover') {
      setIsRecovering(true);
    }
  }, []);

  return (
    <div className="login-page">
      {isRecovering ? <RecoverForm switchToLogin={switchToLogin} /> : <LoginForm switchToRecover={switchToRecover} />}
    </div>
  );
};

export default Login;
