import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './'; // Make sure to create this CSS file for styling

const UpdateProfile = () => {
  const [userData, setUserData] = useState({ firstName: '', lastName: '', email: '' });
  const [loading, setLoading] = useState(true);
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  // Function to fetch user data
  const fetchUserData = async () => {
    const cachedData = localStorage.getItem('userData');
    if (cachedData) {
      setUserData(JSON.parse(cachedData));
      setLoading(false); // Skip loading state if cached
    } else {
      try {
        const response = await axios.get('http://localhost:5000/api/profile'); // Adjust URL as necessary
        setUserData(response.data);
        localStorage.setItem('userData', JSON.stringify(response.data)); // Cache data
      } catch (error) {
        console.error('Error fetching user data:', error);
        setErrorMessage('Failed to load user data.');
      } finally {
        setLoading(false);
      }
    }
  };

  // Fetch user data on mount
  useEffect(() => {
    fetchUserData();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSuccessMessage('');
    setErrorMessage('');

    try {
      const response = await axios.put('http://localhost:5000/api/profile', userData); // Adjust URL as necessary
      setSuccessMessage('Profile updated successfully!');
      localStorage.setItem('userData', JSON.stringify(response.data)); // Update cache after successful update
    } catch (error) {
      console.error('Error updating profile:', error);
      setErrorMessage('Failed to update profile.');
    }
  };

  if (loading) {
    return <div>Loading...</div>; // Loading indicator
  }

  return (
    <div className="update-profile-container">
      <h1>Update Profile</h1>
      <form onSubmit={handleSubmit}>
        <div className="input-group">
          <label htmlFor="firstName">First Name:</label>
          <input
            type="text"
            id="firstName"
            name="firstName"
            value={userData.firstName}
            onChange={handleChange}
            required
          />
        </div>
        <div className="input-group">
          <label htmlFor="lastName">Last Name:</label>
          <input
            type="text"
            id="lastName"
            name="lastName"
            value={userData.lastName}
            onChange={handleChange}
            required
          />
        </div>
        <div className="input-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={userData.email}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="btn">Update Profile</button>
      </form>
      {successMessage && <p className="success-message">{successMessage}</p>}
      {errorMessage && <p className="error-message">{errorMessage}</p>}
    </div>
  );
};

export default UpdateProfile;
