// FRONTEND/src/Components/ContactUs/ContactUs.jsx
import React, { useState, useEffect, useContext } from 'react';
import AOS from 'aos';
import 'aos/dist/aos.css'; // AOS styles
import './ContactUs.css'; // CSS import

// Assuming there's a UserContext or AuthContext to provide user information
import { UserContext } from '../../Context/UserContext';

const ContactUs = () => {
  const { user } = useContext(UserContext); // Access logged-in user info
  const [formData, setFormData] = useState({ 
    name: user ? user.name : '', 
    email: user ? user.email : '', 
    message: '' 
  });

  useEffect(() => {
    AOS.init({ duration: 1000, once: true });
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
  };

  return (
    <div className="contact-us-container" data-aos="fade-up">
      <h3 data-aos="fade-up">Contact Us</h3>
      <form onSubmit={handleSubmit}>
        <div data-aos="fade-up">
          <label>Name:</label>
          <input 
            type="text" 
            name="name" 
            value={formData.name} 
            onChange={handleChange} 
            required 
            disabled={!!user} // Disable if user is logged in
          />
        </div>
        <div data-aos="fade-up">
          <label>Email:</label>
          <input 
            type="email" 
            name="email" 
            value={formData.email} 
            onChange={handleChange} 
            required 
            disabled={!!user} // Disable if user is logged in
          />
        </div>
        <div data-aos="fade-up">
          <label>Message:</label>
          <textarea 
            name="message" 
            value={formData.message} 
            onChange={handleChange} 
            required 
          />
        </div>
        <button type="submit" data-aos="fade-up">Submit</button>
      </form>
    </div>
  );
};

export default ContactUs;
