import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { gsap } from 'gsap';
import './Login.css';

const LoginForm = () => {
  const formRef = useRef(null);
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [isForgotPassword, setIsForgotPassword] = useState(false);

  useEffect(() => {
    const elements = formRef.current.querySelectorAll('.animate');
    gsap.fromTo(
      elements,
      { x: () => `${Math.random() * 200 - 100}px`, y: () => `${Math.random() * 200 - 100}px`, opacity: 0 },
      { x: 0, y: 0, opacity: 1, duration: 0.5, stagger: 0.1 }
    );
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      if (isForgotPassword) {
        await axios.post('http://localhost:5000/auth/forgot-password', { email: formData.email });
        setSuccess('Password reset link sent to your email!');
        setIsForgotPassword(false);
      } else {
        const { data } = await axios.post('http://localhost:5000/auth/login', formData);
        localStorage.setItem('auth-token', data.token);
        setSuccess('Login successful!');
        navigate('/');
      }
    } catch (error) {
      console.error('Error:', error);
      setError(error.response?.data?.message || 'There was an error');
    } finally {
      setLoading(false);
    }
  };

  const toggleForgotPassword = () => {
    setIsForgotPassword(!isForgotPassword);
    setFormData({ email: '', password: '' });
    setSuccess('');
    setError('');
  };

  return (
    <div ref={formRef} id="login-form-container" className="form-container">
      <div className="page-header animate">
        <div className="page-header__text-wrapper text-container">
          <h1 className="heading h2">{isForgotPassword ? 'Forgot Password' : 'Login'}</h1>
          <p>{isForgotPassword ? 'Enter your email to recover your password:' : 'Please enter your e-mail and password:'}</p>
        </div>
      </div>
      <div className="page-content page-content--small">
        <div className="account__block-list">
          <div className="account__block-item">
            <form onSubmit={handleSubmit} className="form">
              {isForgotPassword ? (
                <div className="input animate">
                  <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                    placeholder="Your email"
                  />
                </div>
              ) : (
                <>
                  <div className="input animate">
                    <input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleChange}
                      required
                      placeholder="Your email"
                    />
                  </div>
                  <div className="input animate">
                    <input
                      type="password"
                      name="password"
                      value={formData.password}
                      onChange={handleChange}
                      required
                      placeholder="Your password"
                    />
                  </div>
                </>
              )}
              <button type="submit" className="btn" disabled={loading}>
                {loading ? 'Loading...' : isForgotPassword ? 'Send Reset Link' : 'Login'}
              </button>
            </form>
          </div>
          <p className="form-link animate">
            {isForgotPassword ? (
              <span>
                Remember your password?{' '}
                <button onClick={toggleForgotPassword}>Back to login</button>
              </span>
            ) : (
              <span>
                <button onClick={toggleForgotPassword}>Forgot password?</button>
              </span>
            )}
          </p>
          {success && <p className="form-success animate">{success}</p>}
          {error && <p className="form-error animate">{error}</p>}
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
