import React, { useEffect } from 'react';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import './ColorPage.css';

// Register ScrollTrigger plugin
gsap.registerPlugin(ScrollTrigger);

const ColorPage = () => {
  useEffect(() => {
    // GSAP animation for heading
    gsap.fromTo(
      '.color-page h1',
      { opacity: 0, y: -50 },
      { opacity: 1, y: 0, duration: 1, 
        scrollTrigger: {
          trigger: '.color-page h1',
          start: 'top 80%',
          end: 'top 30%',
          scrub: 1,
          markers: true, // Helps debug the issue
        }
      }
    );

    // GSAP animation for paragraph
    gsap.fromTo(
      '.color-page p',
      { opacity: 0, y: 30 },
      { opacity: 1, y: 0, duration: 1, 
        scrollTrigger: {
          trigger: '.color-page p',
          start: 'top 80%',
          end: 'top 50%',
          scrub: 1,
          markers: true, // Helps debug the issue
        }
      }
    );

    // Change background color on scroll up
    ScrollTrigger.create({
      trigger: '.color-page',
      start: 'top top',
      end: 'bottom top',
      onUpdate: (self) => {
        if (self.direction === -1) {
          gsap.to('.color-page', { backgroundColor: '#ffcccb', duration: 0.5 });
        } else {
          gsap.to('.color-page', { backgroundColor: '#f0e6f6', duration: 0.5 });
        }
      },
      markers: true, // Helps debug the issue
    });

  }, []);

  return (
    <div className="color-page">
      <h1>Welcome to the Colorful Page!</h1>
      <p>This section has layered animations for a unique effect.</p>
    </div>
  );
};

export default ColorPage;
