import React, { useEffect } from 'react';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import Freedom from '../Components/Freedom/Freedom';
import Grid from '../Components/Grid/Grid';
import Popular from '../Components/Popular/Popular';
import { WhatsAppChatWidget } from '../Components/WhatsAppChat/WhatsAppChatWidget'; // Adjusted path
import Round from '../Components/Round/Round';
import './Home.css'; // Create or update this CSS file for custom styles

// Register the ScrollTrigger plugin
gsap.registerPlugin(ScrollTrigger);

const Home = () => {
  useEffect(() => {
    // GSAP Timeline for layered animation
    const tl = gsap.timeline({
      scrollTrigger: {
        trigger: '.home-page',
        start: 'top top',
        end: 'bottom top',
        scrub: true,
        pin: true,
        markers: false
      }
    });

    tl.fromTo(
      '.freedom',
      { opacity: 0, y: -100 },
      { opacity: 1, y: 0, duration: 1 },
      0
    )
    .fromTo(
      '.round',
      { opacity: 0, y: -100 },
      { opacity: 1, y: 0, duration: 1 },
      0.3
    )
    .fromTo(
      '.popular',
      { opacity: 0, y: -100 },
      { opacity: 1, y: 0, duration: 1 },
      0.6
    )
    .fromTo(
      '.grid',
      { opacity: 0, y: -100 },
      { opacity: 1, y: 0, duration: 1 },
      0.9
    )
    .fromTo(
      '.whatsapp-chat-widget',
      { opacity: 0, y: -100 },
      { opacity: 1, y: 0, duration: 1 },
      1.2
    );
  }, []);

  return (
    <div className="home-page">
      <div className="freedom">
        <Freedom />
      </div>
      <div className="round">
        <Round />
      </div>
      <br />
      <div className="popular">
        <Popular />
      </div>
      <br />
      <div className="grid">
        <Grid />
      </div>
      <br />
      <div className="whatsapp-chat-widget">
        <WhatsAppChatWidget />
      </div>
    </div>
  );
};

export default Home;
