//FRONTEND\src\Components\account\Login\Login.jsx
import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { gsap } from 'gsap';
import './Login.css';

const LoginForm = ({ switchToRecover }) => {
  const formRef = useRef(null);
  const [formData, setFormData] = useState({ email: '', password: '' });

  useEffect(() => {
    const elements = formRef.current.querySelectorAll('.animate');
    gsap.fromTo(elements,
      { x: () => `${Math.random() * 200 - 100}px`, y: () => `${Math.random() * 200 - 100}px`, opacity: 0 },
      { x: 0, y: 0, opacity: 1, duration: 0.5, stagger: 0.1 }
    );
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/login', formData);
      alert('Login successful!');
      // Optionally redirect to another page
      // window.location.href = '/dashboard';
    } catch (error) {
      console.error('Login error:', error);
      alert(error.response?.data?.message || 'There was an error logging in');
    }
  };

  return (
    <div ref={formRef} id="login-form-container" className="form-container">
      <div className="page-header animate">
        <div className="page-header__text-wrapper text-container">
          <h1 className="heading h2">Login</h1>
          <p>Please enter your e-mail and password:</p>
        </div>
      </div>
      <div className="page-content page-content--small">
        <div className="account__block-list">
          <div className="account__block-item">
            <form onSubmit={handleSubmit} className="form">
              <div className="input animate">
                <input
                  type="email"
                  id="customer[email]"
                  autoComplete="email"
                  className="input__field"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
                <label htmlFor="customer[email]" className="input__label">E-mail</label>
              </div>
              <div className="input animate">
                <input
                  type="password"
                  id="customer[password]"
                  className="input__field"
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  required
                  autoComplete="current-password"
                />
                <label htmlFor="customer[password]" className="input__label">Password</label>
              </div>
              <button type="button" className="input__field-link link text--xsmall text--subdued forgot-password animate" onClick={switchToRecover}>Forgot password?</button>
              <button type="submit" className="form__submit button button--primary button--full animate">
                <span className="loader-button__text">Login</span>
              </button>
              <span className="form__secondary-action text--subdued animate">New customer? <a href="/Register" className="link">Create an account</a></span>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

const RecoverForm = ({ switchToLogin }) => {
  const formRef = useRef(null);
  const [email, setEmail] = useState('');

  useEffect(() => {
    const elements = formRef.current.querySelectorAll('.animate');
    gsap.fromTo(elements,
      { x: () => `${Math.random() * 200 - 100}px`, y: () => `${Math.random() * 200 - 100}px`, opacity: 0 },
      { x: 0, y: 0, opacity: 1, duration: 0.5, stagger: 0.1 }
    );
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/recover', { email });
      alert(response.data.message);
    } catch (error) {
      console.error('Recovery error:', error);
      alert(error.response?.data?.message || 'There was an error processing the recovery request');
    }
  };

  return (
    <div ref={formRef} id="recover-form-container" className="form-container">
      <div className="page-header animate">
        <div className="page-header__text-wrapper text-container">
          <h1 className="heading h2">Recover password</h1>
          <p>Please enter your e-mail:</p>
        </div>
      </div>
      <div className="page-content page-content--small">
        <form onSubmit={handleSubmit} className="form">
          <div className="input animate">
            <input
              type="email"
              id="customer[recover_email]"
              className="input__field"
              name="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <label htmlFor="customer[recover_email]" className="input__label">E-mail</label>
          </div>
          <button type="submit" className="form__submit button button--primary button--full animate">
            <span className="loader-button__text">Recover</span>
          </button>
          <span className="form__secondary-action text--subdued animate">Remember your password? <button type="button" className="link" onClick={switchToLogin}>Back to login</button></span>
        </form>
      </div>
    </div>
  );
};

const Login = () => {
  const [isRecovering, setIsRecovering] = useState(false);

  const switchToRecover = () => setIsRecovering(true);
  const switchToLogin = () => setIsRecovering(false);

  useEffect(() => {
    if (window.location.hash === '#recover') {
      setIsRecovering(true);
    }
  }, []);

  useEffect(() => {
    const formContainer = document.querySelector('.form-container');
    gsap.fromTo(formContainer,
      { opacity: 0, y: -30 },
      { opacity: 1, y: 0, duration: 0.5 }
    );
  }, [isRecovering]);

  return (
    <div className="login-page">
      {isRecovering ? <RecoverForm switchToLogin={switchToLogin} /> : <LoginForm switchToRecover={switchToRecover} />}
    </div>
  );
};

export default Login;
