import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
  });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem('auth-token');
        if (!token) {
          navigate('/login');
          return;
        }

        const response = await axios.get('http://localhost:5000/auth/user', {
          headers: { 'Authorization': `Bearer ${token}` }
        });

        setUser(response.data);
        setFormData({
          name: `${response.data.firstName} ${response.data.lastName}`,
          email: response.data.email,
          password: ''
        });
        setLoading(false);
      } catch (error) {
        console.error('Error fetching user data:', error);
        setLoading(false);
      }
    };

    fetchUserData();
  }, [navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.name || !formData.email) {
      alert("Name and email are required.");
      return;
    }

    try {
      const token = localStorage.getItem('auth-token');
      await axios.patch('http://localhost:5000/auth/user', {
        firstName: formData.name.split(' ')[0],
        lastName: formData.name.split(' ')[1] || '',
        email: formData.email,
      }, {
        headers: { 'Authorization': `Bearer ${token}` }
      });

      setEditing(false);
      setUser({ ...user, firstName: formData.name.split(' ')[0], lastName: formData.name.split(' ')[1] || '', email: formData.email });
      alert('Profile updated successfully');
    } catch (error) {
      console.error('Error updating user data:', error);
      const errorMessage = error.response?.data?.message || 'Failed to update profile. Please try again.';
      alert(errorMessage);
    }
  };

  const handlePasswordChange = async (e) => {
    e.preventDefault();
    if (!formData.password) {
      alert("New password is required.");
      return;
    }

    try {
      const token = localStorage.getItem('auth-token');
      await axios.patch('http://localhost:5000/auth/user/change-password', { newPassword: formData.password }, {
        headers: { 'Authorization': `Bearer ${token}` }
      });

      alert('Password changed successfully');
      setFormData({ ...formData, password: '' });
    } catch (error) {
      console.error('Error changing password:', error);
      alert('Failed to change password. Please try again.');
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="profile">
      <h1>User Profile</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            disabled={!editing}
          />
        </div>
        <div className="form-group">
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            disabled={!editing}
          />
        </div>
        {editing ? (
          <button type="submit">Save Changes</button>
        ) : (
          <button type="button" onClick={() => setEditing(true)}>Edit Profile</button>
        )}
      </form>

      <h2>Change Password</h2>
      <form onSubmit={handlePasswordChange}>
        <div className="form-group">
          <label>New Password:</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
          />
        </div>
        <button type="submit">Change Password</button>
      </form>
    </div>
  );
};

export default Profile;
