// FRONTEND/src/Components/ContactUs/ContactUs.jsx
import React, { useState, useEffect } from 'react';
import useDocumentTitle from '../../useDocumentTitle'; // Adjust the path based on where your hook is located
import AOS from 'aos';
import 'aos/dist/aos.css'; // AOS styles
import './ContactUs.css'; // Corrected CSS import

const ContactUs = () => {
  useDocumentTitle("Contact Us - Overlays Clothing"); // Set the page title

  const [formData, setFormData] = useState({ name: '', email: '', message: '' });

  useEffect(() => {
    AOS.init({ duration: 1000, once: true }); // Initialize AOS animations
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic
    console.log(formData);
  };

  return (
    <div className="contact-us-container" data-aos="fade-up"> {/* AOS animation for the whole form container */}
      <h3 data-aos="fade-up">Contact Us</h3> {/* AOS for the title */}
      <form onSubmit={handleSubmit}>
        <div data-aos="fade-up"> {/* AOS animation for each input field */}
          <label>Name:</label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} required />
        </div>
        <div data-aos="fade-up">
          <label>Email:</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} required />
        </div>
        <div data-aos="fade-up">
          <label>Message:</label>
          <textarea name="message" value={formData.message} onChange={handleChange} required />
        </div>
        <button type="submit" data-aos="fade-up">Submit</button> {/* AOS for the submit button */}
      </form>
    </div>
  );
};

export default ContactUs;
