import React, { useState } from 'react';
import './TextWithIconsCarousel.css';

const IconCarousel = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const slides = [
    {
      id: 0,
      imgSrc: "//overlaysnow.com/cdn/shop/files/truck_1_100x.png?v=1679920131",
      altText: "Free & Fast Delivery",
      heading: "Free & Fast Delivery",
      text: "Shipping within 48 hours across India."
    },
    {
      id: 1,
      imgSrc: "//overlaysnow.com/cdn/shop/files/retweet_100x.png?v=1679920531",
      altText: "Return Policy",
      heading: "Return Policy",
      text: "Returns within 7 days."
    },
    {
      id: 2,
      imgSrc: "//overlaysnow.com/cdn/shop/files/mail_100x.png?v=1679920559",
      altText: "Contact us",
      heading: "Contact us",
      text: "Write us at support@kkclothing.com"
    }
  ];

  const handleDotClick = (index) => {
    setCurrentSlide(index);
    document.getElementById(`block-static-text-with-icons-static-text-with-icons-${index}`).scrollIntoView({ behavior: 'smooth', block: 'nearest' });
  };

  return (
    <div id="shopify-section-static-text-with-icons" className="shopify-section shopify-section--text-with-icons">
      <section className="section">
        <div className="section__color-wrapper">
          <div className="container">
            <div className="text-with-icons">
              <div className="text-with-icons__list hide-scrollbar">
                {slides.map(slide => (
                  <div key={slide.id} id={`block-static-text-with-icons-static-text-with-icons-${slide.id}`} className="text-with-icons__item">
                    <div className="text-with-icons__icon-wrapper">
                      <img className="text-with-icons__custom-icon" src={slide.imgSrc} alt={slide.altText} />
                    </div>
                    <div className="text-with-icons__content-wrapper">
                      <p className="heading heading--small">{slide.heading}</p>
                      <p>{slide.text}</p>
                    </div>
                  </div>
                ))}
              </div>
              <div className="text-with-icons__dots dots-nav dots-nav--centered hidden-lap-and-up">
                {slides.map((_, index) => (
                  <button key={index} className="dots-nav__item tap-area" aria-current={currentSlide === index} onClick={() => handleDotClick(index)}>
                    <span className="visually-hidden">Go to slide {index + 1}</span>
                  </button>
                ))}
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default IconCarousel;
