// FRONTEND/src/Components/ContactUs/ContactUs.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AOS from 'aos';
import 'aos/dist/aos.css';
import './ContactUs.css';

const ContactUs = () => {
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    // Initialize AOS animations
    AOS.init({ duration: 1000, once: true });

    // Fetch user data for the form
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem('auth-token');
        const response = await axios.get('http://localhost:5000/api/user', {
          headers: { Authorization: `Bearer ${token}` },
        });

        // Set form data with the user's full name and email
        setFormData({
          name: `${response.data.firstName} ${response.data.lastName}`,
          email: response.data.email,
          message: '',
        });
      } catch (err) {
        setError('Failed to load user data. Please try again.');
        console.error('Error fetching user data:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
    // Handle form submission (e.g., send to API or backend server)
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="contact-us-container" data-aos="fade-up">
      <h3 data-aos="fade-up">Contact Us</h3>
      <form onSubmit={handleSubmit}>
        <div data-aos="fade-up">
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>
        <div data-aos="fade-up">
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div data-aos="fade-up">
          <label>Message:</label>
          <textarea
            name="message"
            value={formData.message}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" data-aos="fade-up">
          Submit
        </button>
      </form>
    </div>
  );
};

export default ContactUs;
