import React from 'react';
import './App.css';
import Navbar from './Components/Navbar/Navbar';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import Home from './Pages/Home';
import ShopAll from './Pages/ShopAll';
import Men from './Pages/Men';
import Women from './Pages/Women';
import ContactUs from './Pages/ContactUs';
import Login from './Pages/Login';
import Register from './Pages/Register'; 
import Cart from './Pages/Cart';
import ConfirmEmail from './Pages/ConfirmEmail'; // Import ConfirmEmail component
import IconCarousel from './Components/TextWithIconsCarousel/TextWithIconsCarousel';
import Footer from './Components/Footer/Footer';
import { WhatsAppChatWidget } from './Components/WhatsAppChat/WhatsAppChatWidget';
import AboutUs from './Components/AboutUs/AboutUs';
import TermsAndConditions from './Components/AboutUs/TermsAndConditions';
import PrivacyPolicy from './Components/AboutUs/PrivacyPolicy';

function App() {
  return (
    <div>
      <BrowserRouter>
        <Navbar />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/shop" element={<ShopAll />} />
          <Route path="/men" element={<Men />} />
          <Route path="/women" element={<Women />} />
          <Route path="/contact" element={<ContactUs />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/cart" element={<Cart />} />
          <Route path="/aboutus" element={<AboutUs />} />
          <Route path="/terms" element={<TermsAndConditions />} />
          <Route path="/confirm/:token" element={<ConfirmEmail />} /> {/* Use element instead of component */}
          <Route path="/privacy" element={<PrivacyPolicy />} />
        </Routes>
        <WhatsAppChatWidget />
        <IconCarousel />
        <Footer />
      </BrowserRouter>
    </div>
  );
}

export default App;
