import React, { useState } from 'react';
import axios from 'axios';
import './Registration.css';

const Registration = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: ''
  });
  const [showPassword, setShowPassword] = useState(false);
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage(''); // Clear previous messages
    try {
      const response = await axios.post('/api/register', formData);
      console.log(response.data);
      setMessage('Registration successful! Please log in.');
      // Optionally redirect to login page or clear form
      setFormData({
        firstName: '',
        lastName: '',
        email: '',
        password: ''
      });
    } catch (error) {
      console.error('There was an error registering the user!', error);
      setMessage('Error registering user. Please try again.');
    }
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  return (
    <section>
      <div className="reg-container">
        <div className="page-header">
          <div className="page-header__text-wrapper text-container">
            <h1 className="heading h2">Register</h1>
            <p>Please fill in the fields below:</p>
          </div>
        </div>
        <div className="page-content page-content--small">
          <div className="account__block-list">
            <div className="account__block-item">
              <form onSubmit={handleSubmit} className="form">
                <div className="input">
                  <input
                    type="text"
                    id="firstName"
                    name="firstName"
                    className="input__field"
                    value={formData.firstName}
                    onChange={handleChange}
                    required
                    autoComplete="given-name"
                  />
                  <label htmlFor="firstName" className="input__label">First name</label>
                </div>
                <div className="input">
                  <input
                    type="text"
                    id="lastName"
                    name="lastName"
                    className="input__field"
                    value={formData.lastName}
                    onChange={handleChange}
                    required
                    autoComplete="family-name"
                  />
                  <label htmlFor="lastName" className="input__label">Last name</label>
                </div>
                <div className="input">
                  <input
                    type="email"
                    id="email"
                    name="email"
                    className="input__field"
                    value={formData.email}
                    onChange={handleChange}
                    required
                    autoComplete="email"
                  />
                  <label htmlFor="email" className="input__label">E-mail</label>
                </div>
                <div className="input">
                  <input
                    type={showPassword ? 'text' : 'password'}
                    id="password"
                    name="password"
                    className="input__field"
                    value={formData.password}
                    onChange={handleChange}
                    required
                    autoComplete="new-password"
                  />
                  <label htmlFor="password" className="input__label">Password</label>
                  <button
                    type="button"
                    className="input__field-link link text--xsmall text--subdued toggle-password"
                    onClick={togglePasswordVisibility}
                  >
                    {showPassword ? 'Hide' : 'Show'}
                  </button>
                </div>
                <button type="submit" className="form__submit button button--primary button--full">
                  <span className="loader-button__text">Create account</span>
                </button>
                <span className="form__secondary-action text--subdued">
                  Already have an account? <a href="/login" className="link">Login</a>
                </span>
              </form>
              {message && <p className="message">{message}</p>}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Registration;
