import React from 'react';
import { useState, useEffect } from 'react';
import './Freedom.css';
import bannerImage1 from '../Assets/Freedom_For_You_Desktop_Banner__2.jpg';
import bannerImage2 from '../Assets/Dreams_Sleeveless_Banner_Desktop_2_1.jpg';
import bannerImage3 from '../Assets/Free_Spirit_Back_Desktop_Banner.jpg';

const slides = [
  {
    id: 'block-template--17635245719804__slideshow_HiKTMg-image_pn83nq',
    image: bannerImage1,
    link: '/collections/heavy-weight',
  },
  {
    id: 'block-template--17635245719804__slideshow_HiKTMg-image_jreqa4',
    image: bannerImage2,
    link: '/collections/sleeveless',
  },
  {
    id: 'block-template--17635245719804__slideshow_HiKTMg-image_jRMm8F',
    image: bannerImage3,
    link: '/collections/tshirts-1',
  },
];

const Freedom = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000); // 5 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="shopify-section shopify-section--slideshow">
      <section className="section section--flush">
        <div className="slideshow slideshow--auto" style={{ '--section-animation-play-state': 'running' }}>
          <div className="slideshow__slide-list">
            {slides.map((slide, index) => (
              <a
                key={slide.id}
                href={slide.link}
                className={`slideshow__slide ${index === currentSlide ? 'visible' : ''}`}
                style={{ '--image-aspect-ratio': '2.0', '--mobile-image-aspect-ratio': '2.0' }}
              >
                <div className="slideshow__image-wrapper">
                  <img
                    src={slide.image}
                    alt=""
                    className="slideshow__image"
                    sizes="100vw"
                  />
                </div>
              </a>
            ))}
          </div>
          <div className="slideshow__nav container">
            {slides.map((slide, index) => (
              <button
                key={slide.id}
                className={`slideshow__progress-bar ${index === currentSlide ? 'active' : ''}`}
                aria-controls={slide.id}
                aria-current={index === currentSlide}
                onClick={() => setCurrentSlide(index)}
              >
                <span className="visually-hidden">Go to slide {index + 1}</span>
              </button>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default Freedom;
