import React, { useEffect } from 'react';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

// Import your components
import Freedom from '../Components/Freedom/Freedom';
import Grid from '../Components/Grid/Grid';
import Popular from '../Components/Popular/Popular';
import { WhatsAppChatWidget } from '../Components/WhatsAppChat/WhatsAppChatWidget';
import Round from '../Components/Round/Round';

// Register GSAP ScrollTrigger plugin
gsap.registerPlugin(ScrollTrigger);

const Home = () => {
  useEffect(() => {
    // Animate the 'Freedom' component
    gsap.from('.freedom-component', {
      opacity: 0,
      y: 50,
      duration: 1,
      scrollTrigger: {
        trigger: '.freedom-component',
        start: 'top 80%',
        toggleActions: 'play none none reverse',
      },
    });

    // Animate the 'Round' component
    gsap.from('.round-component', {
      opacity: 0,
      x: -100,
      duration: 1.2,
      scrollTrigger: {
        trigger: '.round-component',
        start: 'top 85%',
        toggleActions: 'play none none reverse',
      },
    });

    // Animate the 'Popular' component
    gsap.from('.popular-component', {
      opacity: 0,
      y: 100,
      duration: 1.5,
      scrollTrigger: {
        trigger: '.popular-component',
        start: 'top 80%',
        toggleActions: 'play none none reverse',
      },
    });

    // Animate the 'Grid' component
    gsap.from('.grid-component', {
      opacity: 0,
      scale: 0.9,
      duration: 1.2,
      scrollTrigger: {
        trigger: '.grid-component',
        start: 'top 80%',
        toggleActions: 'play none none reverse',
      },
    });

    // Animate the 'WhatsAppChatWidget' component
    gsap.from('.whatsapp-chat-widget', {
      opacity: 0,
      x: 100,
      duration: 1.3,
      scrollTrigger: {
        trigger: '.whatsapp-chat-widget',
        start: 'top 90%',
        toggleActions: 'play none none reverse',
      },
    });
  }, []);

  return (
    <div>
      <div className="freedom-component">
        <Freedom />
      </div>
      <div className="round-component">
        <Round />
      </div>
      <br />
      <div className="popular-component">
        <Popular />
      </div>
      <br />
      <div className="grid-component">
        <Grid />
      </div>
      <br />
      <div className="whatsapp-chat-widget">
        <WhatsAppChatWidget />
      </div>
    </div>
  );
};

export default Home;
