import React, { useState, useEffect, useRef, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { gsap } from 'gsap';
import { CartContext } from '../Context/CartContext'; // Assuming you have a CartContext
import './Login.css';

const LoginForm = () => {
  const formRef = useRef(null);
  const navigate = useNavigate();
  const { setCartItems } = useContext(CartContext); // Use context to manage cart items
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [isForgotPassword, setIsForgotPassword] = useState(false);

  useEffect(() => {
    const elements = formRef.current.querySelectorAll('.animate');
    gsap.fromTo(
      elements,
      { x: () => `${Math.random() * 200 - 100}px`, y: () => `${Math.random() * 200 - 100}px`, opacity: 0 },
      { x: 0, y: 0, opacity: 1, duration: 0.5, stagger: 0.1 }
    );
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(''); // Clear previous errors
    setSuccess(''); // Clear previous success messages
    setLoading(true); // Start loading

    try {
      if (isForgotPassword) {
        // Handle forgot password
        await axios.post('http://localhost:5000/auth/forgot-password', { email: formData.email });
        setSuccess('Password reset link sent to your email!'); // Display success message
        setIsForgotPassword(false); // Switch back to login form
      } else {
        // Handle login
        const { data } = await axios.post('http://localhost:5000/auth/login', formData);
        localStorage.setItem('auth-token', data.token); // Store the token
        setCartItems(data.cart); // Set cart items from login response
        setSuccess('Login successful!'); // Display success message
        navigate('/'); // Redirect to home page
      }
    } catch (error) {
      console.error('Error:', error);
      setError(error.response?.data?.message || 'An error occurred.'); // Display error message
    } finally {
      setLoading(false); // Stop loading
    }
  };

  return (
    <div ref={formRef} className="login-form">
      <h2>{isForgotPassword ? 'Forgot Password' : 'Login'}</h2>
      <form onSubmit={handleSubmit}>
        {isForgotPassword ? (
          <>
            <input
              type="email"
              name="email"
              placeholder="Enter your email"
              value={formData.email}
              onChange={handleChange}
              required
            />
            <button type="submit" disabled={loading}>
              {loading ? 'Sending...' : 'Send Reset Link'}
            </button>
          </>
        ) : (
          <>
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
              required
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              required
            />
            <button type="submit" disabled={loading}>
              {loading ? 'Logging in...' : 'Login'}
            </button>
            <p onClick={() => setIsForgotPassword(true)}>Forgot Password?</p>
          </>
        )}
        {error && <p className="error">{error}</p>}
        {success && <p className="success">{success}</p>}
      </form>
    </div>
  );
};

export default LoginForm;
