import React, { useState, useEffect } from 'react';
import AOS from 'aos';
import axios from 'axios';
import 'aos/dist/aos.css';
import './ContactUs.css';

const ContactUs = () => {
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    AOS.init({ duration: 1000, once: true });

    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem('auth-token');
        const response = await axios.get('http://localhost:5000/api/user', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(response.data);
        setFormData({
          name: `${response.data.firstName} ${response.data.lastName}`,
          email: response.data.email,
          message: '',
        });
      } catch (err) {
        setError('Failed to load user data. Please try again.');
        console.error('Error fetching user data:', err);
      } finally {
        setLoading(false); // Stop loading indicator regardless of success or failure
      }
    };

    fetchUserData();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
  };

  if (loading) return <div>Loading user data...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="contact-us-container" data-aos="fade-up">
      <h3 data-aos="fade-up">Contact Us</h3>
      <form onSubmit={handleSubmit}>
        <div data-aos="fade-up">
          <label>Name:</label>
          <input type="text" name="name" value={formData.name} readOnly />
        </div>
        <div data-aos="fade-up">
          <label>Email:</label>
          <input type="email" name="email" value={formData.email} readOnly />
        </div>
        <div data-aos="fade-up">
          <label>Message:</label>
          <textarea
            name="message"
            value={formData.message}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" data-aos="fade-up">Submit</button>
      </form>
    </div>
  );
};

export default ContactUs;
