import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { gsap } from 'gsap';
import './Login.css';

const LoginForm = () => {
  const formRef = useRef(null);
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [isForgotPassword, setIsForgotPassword] = useState(false);

  useEffect(() => {
    const elements = formRef.current.querySelectorAll('.animate');
    gsap.fromTo(
      elements,
      { x: () => `${Math.random() * 200 - 100}px`, y: () => `${Math.random() * 200 - 100}px`, opacity: 0 },
      { x: 0, y: 0, opacity: 1, duration: 0.5, stagger: 0.1 }
    );
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(''); // Clear previous errors
    setSuccess(''); // Clear previous success messages
    setLoading(true); // Start loading

    try {
      if (isForgotPassword) {
        // Handle forgot password
        await axios.post('http://localhost:5000/auth/forgot-password', { email: formData.email });
        setSuccess('Password reset link sent to your email!'); // Display success message
        setIsForgotPassword(false); // Switch back to login form
      } else {
        // Handle login
        const { data } = await axios.post('http://localhost:5000/auth/login', formData);
        localStorage.setItem('auth-token', data.token); // Store the token
        setSuccess('Login successful!'); // Display success message
        navigate('/'); // Redirect to home page
      }
    } catch (error) {
      console.error('Error:', error);
      setError(error.response?.data?.message || 'There was an error'); // Display error
    } finally {
      setLoading(false); // Stop loading
    }
  };

  const toggleForgotPassword = () => {
    setIsForgotPassword(!isForgotPassword);
    setFormData({ email: '', password: '' }); // Clear the form data
    setSuccess(''); // Clear previous success messages
    setError(''); // Clear previous errors
  };

  return (
    <div ref={formRef} id="login-form-container" className="form-container">
      <div className="page-header animate">
        <div className="page-header__text-wrapper text-container">
          <h1 className="heading h2">{isForgotPassword ? 'Forgot Password' : 'Login'}</h1>
          <p>{isForgotPassword ? 'Enter your email to recover your password:' : 'Please enter your e-mail and password:'}</p>
        </div>
      </div>
      <div className="page-content page-content--small">
        <div className="account__block-list">
          <div className="account__block-item">
            <form onSubmit={handleSubmit} className="form">
              {isForgotPassword ? (
                <div className="input animate">
                  <input
                    type="email"
                    id="customer[email]"
                    autoComplete="email"
                    className="input__field"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                  />
                  <label htmlFor="customer[email]" className="input__label">E-mail</label>
                </div>
              ) : (
                <>
                  <div className="input animate">
                    <input
                      type="email"
                      id="customer[email]"
                      autoComplete="email"
                      className="input__field"
                      name="email"
                      value={formData.email}
                      onChange={handleChange}
                      required
                    />
                    <label htmlFor="customer[email]" className="input__label">E-mail</label>
                  </div>
                  <div className="input animate">
                    <input
                      type="password"
                      id="customer[password]"
                      className="input__field"
                      name="password"
                      value={formData.password}
                      onChange={handleChange}
                      required
                      autoComplete="current-password"
                    />
                    <label htmlFor="customer[password]" className="input__label">Password</label>
                  </div>
                </>
              )}
              {!isForgotPassword && (
                <button type="button" className="input__field-link link text--xsmall text--subdued forgot-password animate" onClick={toggleForgotPassword}>
                  Forgot password?
                </button>
              )}
              <button type="submit" className="form__submit button button--primary button--full animate" disabled={loading}>
                {loading ? <span>Loading...</span> : <span className="loader-button__text">{isForgotPassword ? 'Send Reset Link' : 'Login'}</span>}
              </button>
              {success && <p className="form__success-message">{success}</p>}
              {error && <p className="form__error-message">{error}</p>}
              {!isForgotPassword && (
                <span className="form__secondary-action text--subdued animate">
                  New customer? <a href="/Register" className="link">Create an account</a>
                </span>
              )}
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
