import React, { useState } from 'react';
import './TextWithIconsCarousel.css';

const IconCarousel = () => {
  const [activeIndex, setActiveIndex] = useState(0);
  const items = [
    {
      id: 'item-0',
      icon: '//overlaysnow.com/cdn/shop/files/truck_1_100x.png?v=1679920131',
      heading: 'Free & Fast Delivery',
      text: 'Shipping within 48 hours across India.'
    },
    {
      id: 'item-1',
      icon: '//overlaysnow.com/cdn/shop/files/retweet_100x.png?v=1679920531',
      heading: 'Return Policy',
      text: 'Returns within 7 days.'
    },
    {
      id: 'item-2',
      icon: '//overlaysnow.com/cdn/shop/files/mail_100x.png?v=1679920559',
      heading: 'Contact us',
      text: 'Write us at support@kkclothing.com'
    }
  ];

  const handleDotClick = (index) => {
    setActiveIndex(index);
  };

  return (
    <div id="shopify-section-static-text-with-icons" className="shopify-section shopify-section--text-with-icons">
      <section className="section">
        <div className="section__color-wrapper">
          <div className="container">
            <div className="text-with-icons">
              <div className="text-with-icons__list hide-scrollbar">
                {items.map((item, index) => (
                  <div
                    key={item.id}
                    className={`text-with-icons__item ${index === activeIndex ? 'active' : ''}`}
                    style={{ display: index === activeIndex ? 'block' : 'none' }}
                  >
                    <div className="text-with-icons__icon-wrapper">
                      <img className="text-with-icons__custom-icon" src={item.icon} alt={item.heading} />
                    </div>
                    <div className="text-with-icons__content-wrapper">
                      <p className="heading heading--small">{item.heading}</p>
                      <p>{item.text}</p>
                    </div>
                  </div>
                ))}
              </div>
              <div className="text-with-icons__dots dots-nav dots-nav--centered hidden-lap-and-up">
                {items.map((_, index) => (
                  <button
                    key={index}
                    className={`dots-nav__item tap-area ${index === activeIndex ? 'active' : ''}`}
                    aria-current={index === activeIndex}
                    onClick={() => handleDotClick(index)}
                  >
                    <span className="visually-hidden">Go to slide {index + 1}</span>
                  </button>
                ))}
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};
