import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import logo from '../Assets/logo.png';
import profile from '../Assets/profile.png';
import cart_icon from '../Assets/cart_icon.png';

const Navbar = () => {
  const [menu, setMenu] = useState('Home');
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const handleMenuToggle = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div className='navbar'>
      <div className='nav-logo'>
        <img src={logo} alt='logo' />
        <p>OVERLAYS</p>
      </div>
      <div className='nav-menu-toggle' onClick={handleMenuToggle}>
        â˜°
      </div>
      <ul className={`nav-menu ${isMenuOpen ? 'active' : ''}`}>
        <li onClick={() => setMenu('Home')}>
          <Link to='/' className={menu === 'Home' ? 'active-link' : ''}>Home</Link>
          {menu === 'Home' && <hr />}
        </li>
        <li onClick={() => setMenu('Shop')}>
          <Link to='/shop' className={menu === 'Shop' ? 'active-link' : ''}>Shop All</Link>
          {menu === 'Shop' && <hr />}
        </li>
        <li onClick={() => setMenu('Men')}>
          <Link to='/men' className={menu === 'Men' ? 'active-link' : ''}>Men</Link>
          {menu === 'Men' && <hr />}
        </li>
        <li onClick={() => setMenu('Women')}>
          <Link to='/women' className={menu === 'Women' ? 'active-link' : ''}>Women</Link>
          {menu === 'Women' && <hr />}
        </li>
        <li onClick={() => setMenu('Contact')}>
          <Link to='/contact' className={menu === 'Contact' ? 'active-link' : ''}>Contact Us</Link>
          {menu === 'Contact' && <hr />}
        </li>
      </ul>
      <div className='nav-login-cart'>
        <Link to='/Login'><img src={profile} alt='profile icon' className='icon' /></Link>
        <Link to='/cart'><img src={cart_icon} alt='cart icon' /></Link>
        <div className='nav-cart-count'>0</div>
      </div>
    </div>
  );
};

export default Navbar;
