import React, { useContext, useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import './ProductDisplay.css';
import { ShopContext } from '../../Context/ShopContext';

const ProductDisplay = () => {
  const { productId } = useParams(); // Get productId from URL
  const { all_product, addToCart } = useContext(ShopContext);
  const history = useHistory(); // Use history to navigate
  const [selectedSize, setSelectedSize] = useState(null); // State for selected size

  // Find the product based on the productId from the URL
  const product = all_product.find((item) => item.id === Number(productId));

  if (!product) {
    return (
      <div className="product-not-found">
        <h2>Product not found</h2>
        <p>Sorry, we couldn't find the product you're looking for.</p>
        <button onClick={() => history.push('/products')}>Back to Products</button> {/* Navigate to products list */}
      </div>
    );
  }

  const handleSizeSelection = (size) => {
    setSelectedSize(size); // Update the selected size
  };

  const handleAddToCart = () => {
    if (selectedSize) {
      addToCart(product.id, selectedSize); // Pass selected size when adding to cart
    } else {
      alert('Please select a size first');
    }
  };

  return (
    <div className="productdisplay">
      <div className="productdisplay-left">
        <div className="productdisplay-img-list">
          {/* Displaying different images for product */}
          {product.images.map((image, index) => (
            <img key={index} src={image} alt={`${product.name} ${index + 1}`} />
          ))}
        </div>
        <div className="productdisplay-img">
          <img className="productdisplay-main-img" src={product.image} alt={product.name} />
        </div>
      </div>
      <div className="productdisplay-right">
        <h1>{product.name}</h1>
        <div className="productdisplay-right-stars">
          {/* Dynamically creating the stars based on product rating */}
          {[...Array(5)].map((_, index) => (
            <img
              key={index}
              src={index < product.rating ? product.star_icon : product.star_dull_icon}
              alt="star"
            />
          ))}
          <p>({product.reviews})</p>
        </div>
        <div className="productdisplay-right-prices">
          <div className="productdisplay-right-price-old">₹{product.old_price}</div>
          <div className="productdisplay-right-price-new">₹{product.new_price}</div>
        </div>
        <div className="productdisplay-right-description">
          {product.description}
        </div>
        <div className="productdisplay-right-size">
          <h1>Select Size</h1>
          <div className="productdisplay-right-sizes">
            {['S', 'M', 'L', 'XL', 'XXL'].map((size) => (
              <div
                key={size}
                className={`size-option ${selectedSize === size ? 'selected' : ''}`}
                onClick={() => handleSizeSelection(size)}
              >
                {size}
              </div>
            ))}
          </div>
        </div>
        <button onClick={handleAddToCart}>ADD TO CART</button>
        <p className="productdisplay-right-category">
          <span>Category :</span> {product.category}
        </p>
        <p className="productdisplay-right-category">
          <span>Tags :</span> {product.tags.join(', ')}
        </p>
      </div>
    </div>
  );
};

export default ProductDisplay;
