import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { ShopContext } from '../Context/ShopContext'; // This should match the relative path

const ShopAll = () => {
  const navigate = useNavigate();
  
  // Use context to get all_product if you are storing it there
  const { all_product } = useContext(ShopContext);

  // Handle cases where all_product is undefined or empty
  if (!all_product || all_product.length === 0) {
    return <div>No products available at the moment.</div>; // Handle loading or no products
  }

  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`); // Navigate to product detail page
  };

  return (
    <div className="shopall">
      {all_product.map((product) => (
        <div 
          key={product.id} 
          className="product-card"
          onClick={() => handleProductClick(product.id)} // On product click
        >
          <img src={product.image} alt={product.name} />
          <h2>{product.name}</h2>
          <p>â‚¹{product.new_price}</p>
        </div>
      ))}
    </div>
  );
};

export default ShopAll;
